mapper.select:
select excer_id, bank_id, order_no, content, selection_a, selection_b, selection_c, selection_d, selection_correct, answer, own_domain, own_process, create_user, create_time, update_user, update_time from tb_exercise_info
and bank_id=#{bankId}
and order_no=#{orderNo}
and content like concat('%',#{content},'%')
and own_domain like concat('%',#{ownDomain},'%')
and own_process like concat('%',#{ownProcess},'%')

select count(1)
from tb_exercise_info


    and bank_id=#{bankId}
